import React from 'react';
import Me from '../img/kreyes.jpg';
import ScrollAnimation from 'react-animate-on-scroll';

export default class About extends React.Component {
  componentDidMount() {
    window.scrollTo(0, 0);
  }
  render() {
    return (
      <main>
        <Intro />
      </main>
    );
  }
}

function Intro() {
  return (
    <section className="flex flex-col mt-8 overflow-hidden">
      <div className="md:max-w-3xl mx-auto mt-16 px-8 flex flex-col md:flex-row">
        <ScrollAnimation animateIn='fadeInUp' className="md:w-1/3 mb-8 md:pr-16">
          <img className="rounded-full" src={Me} alt="Katherine and her dog"/>
        </ScrollAnimation>
        <div className="md:w-2/3">
        <h2 className="text-3xl font-bold mb-8">About Me</h2>
        <p>I graduated from <b>Cornell University</b> with an <b>Information Science (HCI)</b> major, <b>Computer Science</b> and <b>Psychology</b> minors.</p>
        <p><b>I love learning</b>, and get giddily excited about podcasts, Maria Popova's inspiring life lessons, and Atlas Obscura's latest obscure discoveries.</p>
        <p><b>I love creating and making things</b>. I drool over beautiful illustrations, photos, videos, DIY's, architecture, any form of creative expressions really. I want to learn them all! At the moment, I'm focused on ballet and watercolor!</p>
        <p><b>Laugh, dance, travel, read, live life to the fullest.</b> I don't sleep enough, but I dream.</p>
        <h2 className="text-3xl font-bold my-8">Work History</h2>

        <p><strong>Gusto</strong> <b>Product Designer</b> &mdash; 1.5 years
  					<br />I design, code, and do everything in between to create a delightful product that our users love!
        </p>
        <p><strong>Quantcast</strong> <b>Senior User Experience Designer</b> &mdash; 2.5 years
            <br />I solved data visualization problems, from user research, information architecture,
            wireframes, interactive prototypes, to visual design, style guide and front-end development.
        </p>
        <p><strong>Apple</strong> <b>User Experience Designer</b> &mdash; 3 years
            <br />I designed and prototyped enterprise software for iPhone, iPad and desktop.
        </p>
        <p><strong>Apple</strong> <b>Front-end UI Engineer</b> &mdash; 1 year
            <br />I made sure the Apple Online Store was fast, interactive, and pixel-perfect.
        </p>
        <p><strong>Cornell University</strong> <b>Web Developer/Designer</b> &mdash; 3 years
            <br />I designed and developed websites for a variety of school organizations.
        </p>
      </div>
      </div>
    </section>
  );
}
